import React from "react";
import { Link, useNavigate } from "react-router-dom";

const Login = ({ setIsLoggedIn }) => {
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();
    setIsLoggedIn(true);
    navigate("/");
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-[#0f0f0f] px-4 relative">
      {/* Controller Image */}
      <img
        src="/game.png"
        alt="Game Controller"
        className="w-[500px] md:w-[600px] z-0 select-none pointer-events-none"
      />

      {/* Form inside Controller */}
      <div className="absolute w-[250px] md:w-[300px] bg-[#1a1a1a]/80 backdrop-blur-md p-6 rounded-xl shadow-lg border border-[#2c2c2c] z-10">
        <h2 className="text-2xl font-bold text-center text-[#6ee7b7] mb-2 tracking-wide">
          Vellichor
        </h2>
        <p className="text-xs text-center text-[#f9c5d1] mb-4 italic">
          “Enter the game…”
        </p>

        <form onSubmit={handleLogin} className="flex flex-col gap-3">
          <input
            type="email"
            placeholder="Email"
            required
            className="p-2 border border-[#2c2c2c] rounded bg-[#0e0e0e] text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-[#6ee7b7] transition-all text-sm"
          />
          <input
            type="password"
            placeholder="Password"
            required
            className="p-2 border border-[#2c2c2c] rounded bg-[#0e0e0e] text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-[#6ee7b7] transition-all text-sm"
          />
          <button
            type="submit"
            className="bg-[#6ee7b7] text-black py-2 rounded-md hover:bg-[#4ade80] transition-all font-medium shadow-md shadow-[#6ee7b760]"
          >
            Login
          </button>
        </form>

        <p className="text-xs text-center mt-3 text-gray-400">
          Don’t have an account?{" "}
          <Link
            to="/signup"
            className="text-[#f9c5d1] font-medium hover:underline"
          >
            Sign up
          </Link>
        </p>
      </div>
    </div>
  );
};

export default Login;
