import React from "react";
import { Link } from "react-router-dom";

const Home = () => {
  // Assuming no authentication yet — you'd later replace this with real auth check
  const isLoggedIn = false;

  return (
    <div className="relative h-screen bg-[#f5f2ee] text-[#5e4b4b] font-serif">
      {/* Top-right corner login/signup */}
      {!isLoggedIn && (
        <div className="absolute top-6 right-10 space-x-4">
          <Link to="/login">
            <button className="px-4 py-2 bg-transparent border border-[#5e4b4b] rounded-full hover:bg-[#5e4b4b] hover:text-white transition">
              Login
            </button>
          </Link>
          <Link to="/signup">
            <button className="px-4 py-2 bg-[#5e4b4b] text-white rounded-full hover:bg-[#3e2f2f] transition">
              Sign Up
            </button>
          </Link>
        </div>
      )}

      {/* Center quote */}
      <div className="flex items-center justify-center h-full text-center px-4">
        <h1 className="text-4xl md:text-5xl lg:text-6xl font-bold max-w-3xl leading-snug">
          "You're just <span className="text-[#a78b71] italic">one step away</span> from your dream role."
        </h1>
      </div>
    </div>
  );
};

export default Home;
