import React, { useState, useEffect } from "react";

const Loader = () => {
  const fullText = "Loading your quest...";
  const [displayedText, setDisplayedText] = useState("");

  useEffect(() => {
    let index = 0;
    const typingInterval = setInterval(() => {
      setDisplayedText((prev) => prev + fullText[index]);
      index++;
      if (index === fullText.length) clearInterval(typingInterval);
    }, 80); // Typing speed in ms

    return () => clearInterval(typingInterval);
  }, []);

  return (
    <div className="flex flex-col items-center justify-center h-screen bg-[#0f0f0f] text-[#f9c5d1] font-mono">
      <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-[#10b981] mb-6"></div>
      <p className="italic text-lg tracking-wide">{displayedText}</p>
    </div>
  );
};

export default Loader;
