import React from "react";
import { Link } from "react-router-dom";
import controllerBg from "../assets/controller.png"; // Place your controller image inside src/assets/

const Login = () => {
  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-b from-gray-900 to-black relative">
      <div
        className="relative bg-no-repeat bg-center bg-contain w-[500px] h-[350px]"
        style={{ backgroundImage: `url(${controllerBg})` }}
      >
        {/* Email Input on Left */}
        <input
          type="email"
          placeholder="Email"
          className="absolute top-[40%] left-[20%] w-40 py-2 px-3 rounded-full text-sm bg-black bg-opacity-60 text-white border border-gray-500 placeholder-gray-300 focus:ring-2 focus:ring-green-400 transition"
        />

        {/* Password Input on Right */}
        <input
          type="password"
          placeholder="Password"
          className="absolute top-[40%] right-[20%] w-40 py-2 px-3 rounded-full text-sm bg-black bg-opacity-60 text-white border border-gray-500 placeholder-gray-300 focus:ring-2 focus:ring-green-400 transition"
        />

        {/* Login Button in Middle */}
        <button
          type="submit"
          className="absolute bottom-[10%] left-1/2 transform -translate-x-1/2 bg-green-600 hover:bg-green-700 text-white py-2 px-5 rounded-full shadow-lg transition"
        >
          Login
        </button>

        {/* Signup Link */}
        <p className="absolute bottom-0 left-1/2 transform -translate-x-1/2 text-xs text-gray-300 mt-2">
          Donâ€™t have an account?{" "}
          <Link to="/signup" className="text-green-400 hover:underline">
            Sign up
          </Link>
        </p>
      </div>
    </div>
  );
};

export default Login;
