import React from "react";
import { Link } from "react-router-dom";

const Home = () => {
  // Placeholder for login state — update this when auth is added
  const isLoggedIn = false;

  return (
    <div className="relative h-screen w-full bg-[#fdfaf6] text-[#3e2f2f] font-serif overflow-hidden">
      {/* Header buttons */}
      {!isLoggedIn && (
        <div className="absolute top-6 right-10 space-x-4 z-10">
          <Link to="/login">
            <button className="px-5 py-2 border border-[#3e2f2f] rounded-full hover:bg-[#3e2f2f] hover:text-white transition-all duration-200">
              Login
            </button>
          </Link>
          <Link to="/signup">
            <button className="px-5 py-2 bg-[#3e2f2f] text-white rounded-full hover:bg-[#2a2222] transition-all duration-200">
              Sign Up
            </button>
          </Link>
        </div>
      )}

      {/* Main content */}
      <div className="flex flex-col items-center justify-center h-full text-center px-4">
        <h1 className="text-4xl md:text-5xl font-bold mb-4">
          You're just <span className="italic text-[#a78b71]">one step away</span> from your dream role.
        </h1>
        <p className="text-lg md:text-xl text-[#5e4b4b] max-w-xl mt-2">
          Explore creative tech careers — from narrative designers to lore crafters. ✨
        </p>

        {/* CTA button (optional) */}
        <Link to="/jobs">
          <button className="mt-8 px-6 py-3 bg-[#a78b71] text-white rounded-full hover:bg-[#8b6b50] transition duration-300">
            Browse Jobs
          </button>
        </Link>
      </div>
    </div>
  );
};

export default Home;
